// Check Schedule & Resolve Window (v3: Multi-hit with Unique ID)
const dayjs = global.get('dayjs');
if (!dayjs) {
    node.error('Day.js not loaded to global context');
    return null;
}

const tz = env.get('TZ') || 'Asia/Bangkok';
const now = dayjs().tz(tz).second(0).millisecond(0);
const settings = msg.payload || {};

if (!settings.schedule || !Array.isArray(settings.schedule.configs)) {
    node.status({ fill: 'grey', shape: 'ring', text: 'No schedule' });
    return null;
}

const todayWeekday = now.day();
const weekdays = settings.schedule.weekdays || [true, true, true, true, true, true, true];
const todayOn = weekdays[todayWeekday] !== false;

const activeConfigs = (settings.schedule.configs || []).filter(c =>
    (c?.active === undefined ? true : !!c.active) && todayOn
);

function toToday(hm) {
    const [H, M] = String(hm || '00:00').split(':').map(Number);
    return now.clone().hour(H).minute(M);
}

const hits = activeConfigs.filter(c => now.isSame(toToday(c.sendAt), 'minute'));

if (hits.length === 0) {
    const nexts = activeConfigs.map(c => String(c.sendAt)).filter(Boolean).join(', ');
    node.status({ fill: 'blue', shape: 'ring', text: `Waiting ${nexts}` });
    return null;
}

for (const hit of hits) {
    const newMsg = {};

    newMsg.report_id = RED.util.generateId();
    newMsg.recipients = settings.recipients || { email: [], telegram: [] };

    const S = toToday(hit.rangeStart);
    const E = toToday(hit.rangeEnd);
    const T = toToday(hit.sendAt);

    let startTs, endTs;
    if (hit.rangeStart === hit.rangeEnd) {
        endTs = E;
        if (T.isBefore(E)) endTs = endTs.subtract(1, 'day');
        startTs = endTs.clone().subtract(24, 'hour');
    } else {
        endTs = E;
        let startCandidate = S;
        if (E.isBefore(S)) startCandidate = S.clone().subtract(1, 'day');
        if (T.isBefore(E)) {
            endTs = endTs.clone().subtract(1, 'day');
            startCandidate = startCandidate.clone().subtract(1, 'day');
        }
        startTs = startCandidate;
    }

    // The old `dotHM` function has been removed as it's no longer needed.

    newMsg.window = {
        start: startTs.format('YYYY-MM-DD HH:mm:ss'),
        end: endTs.format('YYYY-MM-DD HH:mm:ss'),
        startISO: startTs.toISOString(),
        endISO: endTs.toISOString(),
        displayRange: `${hit.rangeStart}‚Äì${hit.rangeEnd}`,
        // üëá This is the line that was changed
        displayLabel: `${hit.rangeStart}-${String(hit.rangeEnd || '00:00').replace(':', ':')}`,
        reportDate: endTs.format('DD/MM/YYYY'),
        label: hit.label || ''
    };

    const labelPart = hit.label ? `_${hit.label}` : '';
    newMsg.attachmentName = `daily${labelPart}_${endTs.format('DDMMYYYY_HHmm')}.csv`;

    node.status({ fill: 'green', shape: 'dot', text: `Run ${hit.sendAt} (${newMsg.window.label})` });

    node.send(newMsg);
}

return null;

// EnsureBothMaps (v2: Concurrent-safe)
const report_id = msg.report_id;
if (!report_id) {
    node.warn("Message missing report_id, cannot join maps safely.");
    return null;
}

const key = msg.topic;
const flowBuf = flow.get('mapsBufObj') || {};

if (!flowBuf[report_id]) {
    flowBuf[report_id] = {};
}

flowBuf[report_id][key] = msg.payload;
flow.set('mapsBufObj', flowBuf);

const reportBuf = flowBuf[report_id];

if (!reportBuf.deviceMap || !reportBuf.pinMap) {
    return null; // Wait for the other part for this specific report
}

// Both parts arrived, create the output message
const out = { 
    payload: { 
        deviceMap: reportBuf.deviceMap, 
        pinMap: reportBuf.pinMap 
    } 
};

// Clean up the buffer for this completed report
delete flowBuf[report_id];
flow.set('mapsBufObj', flowBuf);

// Carry over all essential properties from the original message
out.window = msg.window;
out.recipients = msg.recipients;
out.attachmentName = msg.attachmentName;
out.report_id = msg.report_id; // <-- ‡∏™‡∏≥‡∏Ñ‡∏±‡∏ç‡∏°‡∏≤‡∏Å: ‡∏ï‡πâ‡∏≠‡∏á‡∏™‡πà‡∏á‡∏ï‡πà‡∏≠ ID ‡πÑ‡∏õ‡∏î‡πâ‡∏ß‡∏¢

return out;

let deviceMap = msg.payload && msg.payload.deviceMap;
let pinMap = msg.payload && msg.payload.pinMap;
if ((!deviceMap || !pinMap) && Array.isArray(msg.payload)) {
  const a = msg.payload[0], b = msg.payload[1];
  const looks = (o) => o && typeof o === 'object' && Object.keys(o).some(k => /^room[A-G]$/.test(k) || /^name[A-G]$/.test(k));
  if (looks(a)) { deviceMap = a; pinMap = b; } else if (looks(b)) { deviceMap = b; pinMap = a; }
}
if (!deviceMap || typeof deviceMap !== 'object') { node.error('Device map missing'); return null; }
if (!pinMap || typeof pinMap !== 'object') { node.error('Pin map missing'); return null; }

function buildMacToRoom(map) {
  const out = {};
  for (const L of ['A', 'B', 'C', 'D', 'E', 'F', 'G']) {
    const mac = (map[`room${L}`] || '').trim();
    const name = (map[`name${L}`] || '').trim();
    if (mac && name) out[mac] = name;
  }
  return out;
}

function sensorName(mac, pin) {
  const byMac = pinMap[mac] || {};
  const n = byMac[pin];
  return n && String(n).trim() ? n : `PIN ${pin}`;
}

msg.macToRoom = buildMacToRoom(deviceMap);
msg.roomToMac = Object.fromEntries(Object.entries(msg.macToRoom).map(([mac, room]) => [room, mac]));
msg.allMacs = Object.keys(msg.macToRoom);
msg.sensorName = (mac, p) => sensorName(mac, p);

// --- START: ‡πÇ‡∏Ñ‡πâ‡∏î‡πÅ‡∏Å‡πâ‡πÑ‡∏Ç ---
// ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏ß‡πà‡∏≤‡∏°‡∏µ MAC address ‡∏ó‡∏µ‡πà‡∏à‡∏∞‡πÉ‡∏ä‡πâ‡πÉ‡∏ô‡∏£‡∏≤‡∏¢‡∏á‡∏≤‡∏ô‡∏´‡∏£‡∏∑‡∏≠‡πÑ‡∏°‡πà
if (msg.allMacs.length === 0) {
  node.warn("No active devices found in device map. Stopping report flow.");
  return null; // ‡∏´‡∏¢‡∏∏‡∏î‡∏Å‡∏≤‡∏£‡∏ó‡∏≥‡∏á‡∏≤‡∏ô‡∏Ç‡∏≠‡∏á Flow ‡∏ñ‡πâ‡∏≤‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏≠‡∏∏‡∏õ‡∏Å‡∏£‡∏ì‡πå
}
// --- END: ‡πÇ‡∏Ñ‡πâ‡∏î‡πÅ‡∏Å‡πâ‡πÑ‡∏Ç ---

// ‡∏™‡∏£‡πâ‡∏≤‡∏á String ‡∏Ç‡∏≠‡∏á MAC address ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡πÉ‡∏ä‡πâ‡πÉ‡∏ô SQL (‡∏¢‡∏±‡∏á‡∏Ñ‡∏á‡∏à‡∏≥‡πÄ‡∏õ‡πá‡∏ô‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö Uptime Flow ‡πÉ‡∏´‡∏°‡πà)
msg.allMacsInQuotes = msg.allMacs.map(m => `'${m}'`).join(',');

return msg;

// Process & Structure Data (V11B ‚Äî Priority: DeviceOffline > SensorOffline > Critical > Warning > Time(desc) > Pin(asc) > Name)
// [FIXED] ‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏Å‡∏≤‡∏£‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏Ç‡∏≤‡πÄ‡∏Ç‡πâ‡∏≤‡πÄ‡∏û‡∏∑‡πà‡∏≠‡∏õ‡πâ‡∏≠‡∏á‡∏Å‡∏±‡∏ô‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ó‡∏µ‡πà‡∏¢‡∏±‡∏á‡πÑ‡∏°‡πà‡∏ñ‡∏π‡∏Å Join
const requiredKeys = ['rules', 'events', 'stats', 'statTimes', 'kpis', 'uptime_results'];
const payloadOk = msg.payload && typeof msg.payload === 'object' && !Array.isArray(msg.payload);
const missingKeys = payloadOk ? requiredKeys.filter(k => !msg.payload.hasOwnProperty(k)) : requiredKeys;

if (!payloadOk || missingKeys.length > 0) {
    node.warn(`Received incomplete or malformed data payload. Required keys missing: ${missingKeys.join(', ')}. Halting report generation.`);
    return null; // ‡∏´‡∏¢‡∏∏‡∏î‡∏Å‡∏≤‡∏£‡∏ó‡∏≥‡∏á‡∏≤‡∏ô‡∏Ç‡∏≠‡∏á Flow ‡∏ó‡∏±‡∏ô‡∏ó‡∏µ
}

const { rules, events, stats, statTimes, kpis, uptime_results } = msg.payload;

function buildRanges(rules) {
  const out = { room: {}, sensor: {} };
  for (const r of (rules || [])) {
    const rng = {
      warn: { min: r.warn_min, max: r.warn_max },
      crit: { min: r.crit_min, max: r.crit_max }
    };
    if (r.scope === 'room' && r.sensor_name === 'overall') {
      out.room[r.metric] = rng;
    } else if (r.scope === 'sensor') {
      out.sensor[`${r.mac}|${r.sensor_name}|${r.metric}`] = rng;
    }
  }
  return out;
}
msg.accept = buildRanges(rules || []);

function judge(v, r) {
  if (v == null || !r) return 'unknown';
  const w = r.warn, c = r.crit;
  if (c && ((c.min != null && v < c.min) || (c.max != null && v > c.max))) return 'critical';
  if (w && ((w.min != null && v < w.min) || (w.max != null && v > w.max))) return 'warning';
  return 'ok';
}

const em = { ok: '‚úÖ', warning: '‚ö†Ô∏è', critical: '‚ùå', unknown: '‚ùì' };
const tz = env.get('TZ') || 'Asia/Bangkok';
msg.rooms = [];

// helpers
function toTs(x) { const t = new Date(x).getTime(); return isNaN(t) ? 0 : t; }
function parsePinKey(pinOrName) {
  if (pinOrName == null) return 99999;
  const s = String(pinOrName);
  const m = s.match(/\d+/);
  const n = m ? Number(m[0]) : NaN;
  return isNaN(n) ? 99999 : n;
}

for (const mac of (msg.allMacs || [])) {
  const roomName = msg.macToRoom[mac];
  const room = { name: roomName, mac, overall: {}, sensors: [], alerts: [], kpi: {} };

  // Overall stats
  const overallStat  = (stats || []).find(s => s.mac === mac && s.pin === 'OVERALL') || {};
  const overallTimes = (statTimes || []).find(s => s.mac === mac && s.pin === 'OVERALL') || {};
  room.overall = { ...overallStat, ...overallTimes };

  // Sensor stats
  const sensorStats = (stats || []).filter(s => s.mac === mac && s.pin !== 'OVERALL');
  for (const ss of sensorStats) {
    const sName = msg.sensorName(mac, ss.pin);
    const sTimes = (statTimes || []).find(s => s.mac === mac && s.pin === ss.pin) || {};
    const tRule = msg.accept.sensor[`${mac}|${sName}|temp`];
    const hRule = msg.accept.sensor[`${mac}|${sName}|hum`];
    const tStat = judge(ss.avg_temp, tRule);
    const hStat = judge(ss.avg_hum, hRule);
    room.sensors.push({ ...ss, ...sTimes, name: sName, temp_status: tStat, hum_status: hStat });
  }

  // Sort sensors: abnormal first, then by pin number
  const statusScore = { critical: 2, warning: 1, ok: 0, unknown: -1 };
  room.sensors.sort((a, b) => {
    const scoreA = Math.max(statusScore[a.temp_status], statusScore[a.hum_status]);
    const scoreB = Math.max(statusScore[b.temp_status], statusScore[b.hum_status]);
    if (scoreA !== scoreB) return scoreB - scoreA;
    return String(a.pin).localeCompare(String(b.pin), undefined, { numeric: true });
  });

  // Overall room statuses
  room.overall.temp_status = judge(room.overall.avg_temp, msg.accept.room.temp);
  room.overall.hum_status  = judge(room.overall.avg_hum,  msg.accept.room.hum);

  // Build & sort alerts with new priority
  const alertStatusScore = { critical: 2, warning: 1 };

  function alertRank(a) {
    // 0 = Device Offline, 1 = Sensor Offline, 2 = Other Critical, 3 = Warning, 4 = Others
    if (a.is_device && a.is_offline) return 0;
    if (!a.is_device && a.is_offline) return 1;
    if (a.level === 'critical') return 2;
    if (a.level === 'warning') return 3;
    return 4;
  }

  room.alerts = (events || [])
    .filter(e => e.mac === mac)
    .map(e => {
      const a = { ...e, duration_text: '' };

      const metricLower = String(e.metric || '').toLowerCase();
      const looksConn   = metricLower.includes('connect') || metricLower.includes('offline');
      let offlineFlag   = false;
      if (looksConn) {
        const raw = e.trigger_value;
        if (raw === 0 || String(raw).trim() === '0' || /‡∏≠‡∏≠‡∏ü‡πÑ‡∏•‡∏ô‡πå/i.test(String(raw))) {
          offlineFlag = true;
        }
      }

      if (metricLower === 'temp') { a.metric_desc = '‡∏≠‡∏∏‡∏ì‡∏´‡∏†‡∏π‡∏°‡∏¥'; a.unit = '¬∞C'; }
      else if (metricLower === 'hum') { a.metric_desc = '‡∏Ñ‡∏ß‡∏≤‡∏°‡∏ä‡∏∑‡πâ‡∏ô'; a.unit = '%'; }
      else if (looksConn) {
        a.metric_desc = '‡∏Å‡∏≤‡∏£‡πÄ‡∏ä‡∏∑‡πà‡∏≠‡∏°‡∏ï‡πà‡∏≠'; a.unit = '';
        a.trigger_value = offlineFlag ? '‡∏≠‡∏≠‡∏ü‡πÑ‡∏•‡∏ô‡πå' : (e.trigger_value == 1 ? '‡∏≠‡∏≠‡∏ô‡πÑ‡∏•‡∏ô‡πå' : (e.trigger_value || ''));
        if (offlineFlag) {
          if (!e.closed_at) {
            a.duration_text = ' (‡∏≠‡∏≠‡∏ü‡πÑ‡∏•‡∏ô‡πå‡∏ï‡πà‡∏≠‡πÄ‡∏ô‡∏∑‡πà‡∏≠‡∏á)';
          } else {
            const durationMin = Math.round((toTs(e.closed_at) - toTs(e.opened_at)) / 60000);
            a.duration_text = durationMin > 0 ? ` (${durationMin} ‡∏ô‡∏≤‡∏ó‡∏µ)` : ' (<1 ‡∏ô‡∏≤‡∏ó‡∏µ)';
          }
        }
      } else {
        a.metric_desc = e.metric; a.unit = '';
      }

      a.opened_at_ts = toTs(e.opened_at);
      a.time = new Date(e.opened_at)
        .toLocaleTimeString('th-TH', { hour: '2-digit', minute: '2-digit', timeZone: tz })
        .padStart(5, '0');

      const sensorName = (e.sensor_name || '').trim();
      const nameLower  = sensorName.toLowerCase();
      const isRealDeviceName = (nameLower === 'device');
      const isDeviceType     = (e.sensor_type === 'device');
      a.is_device  = isDeviceType || isRealDeviceName;

      a.is_offline = !!offlineFlag;

      a.pin_sort = parsePinKey(e.pin != null ? e.pin : sensorName);
      a.sensor_name = sensorName || (a.is_device ? room.name : '‡πÑ‡∏°‡πà‡∏£‡∏∞‡∏ö‡∏∏‡∏ä‡∏∑‡πà‡∏≠');

      return a;
    })
    .sort((x, y) => {
      const rx = alertRank(x), ry = alertRank(y);
      if (rx !== ry) return rx - ry;
      const sx = alertStatusScore[x.level] || 0;
      const sy = alertStatusScore[y.level] || 0;
      if (sx !== sy) return sy - sx;
      const tsX = x.opened_at_ts || 0;
      const tsY = y.opened_at_ts || 0;
      if (tsX !== tsY) return tsY - tsX;
      const pinX = x.pin_sort, pinY = y.pin_sort;
      if (pinX !== pinY) return pinX - pinY;
      return String(x.sensor_name || '').localeCompare(String(y.sensor_name || ''), undefined, { numeric: true });
    });

  // KPIs
  const kpiData    = (kpis || []).find(k => k.mac === mac) || {};
  const uptimeData = (uptime_results || []).find(u => u.mac === mac) || {};
  room.kpi.uptime_pct      = uptimeData.uptime_pct;
  room.kpi.latest_data_ts  = kpiData.latest_data_ts;
  room.kpi.open_alerts_count = room.alerts.filter(a => a.status === 'open' || a.status === 'ack').length;

  // Overall status
  let finalStatus = 'ok';
  const hasCriticalAlert = room.alerts.some(a => a.level === 'critical');
  const hasWarningAlert  = room.alerts.some(a => a.level === 'warning');
  if (hasCriticalAlert || room.overall.temp_status === 'critical' || room.overall.hum_status === 'critical') {
    finalStatus = 'critical';
  } else if (hasWarningAlert || room.overall.temp_status === 'warning' || room.overall.hum_status === 'warning') {
    finalStatus = 'warning';
  }
  room.overall_status = finalStatus;
  room.overall_status_emoji = em[finalStatus];

  msg.rooms.push(room);
}

return msg;

