// --- Helper Functions ---
function fmtNum(n, u = '', p = 1, fallback = '-') { if (n == null || isNaN(n)) return fallback; const val = Number(n).toFixed(p); return u ? `${val}&nbsp;${u}` : val; }
function fmtTime(t, fallback = '-') { if (!t) return fallback; const d = new Date(t); if (isNaN(d.getTime())) return fallback; const h = String(d.getHours()).padStart(2, '0'); const m = String(d.getMinutes()).padStart(2, '0'); return `${h}:${m}`; }
function getStatusPill(status) { const pills = { ok: { text: '‡∏õ‡∏Å‡∏ï‡∏¥', class: 'status-pill-ok' }, warning: { text: '‡πÄ‡∏ù‡πâ‡∏≤‡∏£‡∏∞‡∏ß‡∏±‡∏á', class: 'status-pill-warn' }, critical: { text: '‡∏ß‡∏¥‡∏Å‡∏§‡∏ï‡∏¥', class: 'status-pill-crit' } }; const pill = pills[status] || { text: '‚ùì ‡πÑ‡∏°‡πà‡∏ó‡∏£‡∏≤‡∏ö', class: 'status-pill-unknown' }; return `<span class="status-pill ${pill.class}">${pill.text}</span>`; }
