    const sortedAlerts = [...room.alerts].sort((x, y) => {
      const sx = levelScore[x.level] || 0;
      const sy = levelScore[y.level] || 0;
      if (sx !== sy) return sy - sx;
      return (y.opened_at_ts || 0) - (x.opened_at_ts || 0);
    });

    for (const alert of sortedAlerts) {
      const sensorName = alert.sensor_name || '‡πÑ‡∏°‡πà‡∏£‡∏∞‡∏ö‡∏∏‡∏ä‡∏∑‡πà‡∏≠';
      let reasonKey = '';

      const m = String(alert.metric || '').toLowerCase();
