      const rx = alertRank(x), ry = alertRank(y);
      if (rx !== ry) return rx - ry;

      // 1) Level: critical > warning
      const sx = alertStatusScore[x.level] || 0;
      const sy = alertStatusScore[y.level] || 0;
      if (sx !== sy) return sy - sx;

      // 2) Time: newer first
      const tsX = x.opened_at_ts || 0;
      const tsY = y.opened_at_ts || 0;
      if (tsX !== tsY) return tsY - tsX;

      // 3) Sensor pin number ascending
      const pinX = x.pin_sort, pinY = y.pin_sort;
      if (pinX !== pinY) return pinX - pinY;

