const { rules, events, stats, statTimes, kpis, uptime_results } = msg.payload;

function buildRanges(rules) {
  const out = { room: {}, sensor: {} };
  for (const r of (rules || [])) {
    const rng = {
      warn: { min: r.warn_min, max: r.warn_max },
      crit: { min: r.crit_min, max: r.crit_max }
