      else if (metricLower === 'hum') { a.metric_desc = '‡∏Ñ‡∏ß‡∏≤‡∏°‡∏ä‡∏∑‡πâ‡∏ô'; a.unit = '%'; }
      else if (looksConn) {
        a.metric_desc = '‡∏Å‡∏≤‡∏£‡πÄ‡∏ä‡∏∑‡πà‡∏≠‡∏°‡∏ï‡πà‡∏≠'; a.unit = '';
