          } else {
            const durationMin = Math.round((toTs(e.closed_at) - toTs(e.opened_at)) / 60000);
            a.duration_text = durationMin > 0 ? ` (${durationMin} ‡∏ô‡∏≤‡∏ó‡∏µ)` : ' (<1 ‡∏ô‡∏≤‡∏ó‡∏µ)';
          }
        }
      } else {
        a.metric_desc = e.metric; a.unit = '';
      }

      a.opened_at_ts = toTs(e.opened_at);
      a.time = new Date(e.opened_at)
        .toLocaleTimeString('th-TH', { hour: '2-digit', minute: '2-digit', timeZone: tz })
        .padStart(5, '0');

      const sensorName = (e.sensor_name || '').trim();
      const nameLower  = sensorName.toLowerCase();
      const isRealDeviceName = (nameLower === 'device');
