// ========= Reboot All =========
if (cmd === 'reboot_all' || room === 'all' || Array.isArray(p.mac_list)) {
  let macs = Array.isArray(p.mac_list) ? p.mac_list.map(normMAC).filter(Boolean) : null;
  if (!macs || macs.length === 0) {
    const room2mac = flow.get('room2mac') || {};
    macs = Object.values(room2mac).map(normMAC).filter(Boolean);
  }
  if (!macs.length) {
    return [null, { payload: { actionId, status: 'error', message: 'no target MACs' } }];
  }

  // register pending (by actionId + by mac)
  pendingByAction[actionId] = { targets: new Set(macs), seen: new Set() };
  macs.forEach(m => { pendingByMac[m] = actionId; });

  // timeout per actionId
  if (timers[actionId]) clearTimeout(timers[actionId]);
  timers[actionId] = setTimeout(() => {
    const _pAct = flow.get('reboot_pending_by_action') || {};
    const _tim  = flow.get('reboot_timers') || {};
    const st = _pAct[actionId];
    if (st) {
      // cleanup mac->action
      st.targets.forEach(m => { 
        let map = flow.get('reboot_pending_by_mac') || {};
        if (map[m] === actionId) { delete map[m]; flow.set('reboot_pending_by_mac', map); }
      });
      delete _pAct[actionId];
      delete _tim[actionId];
      flow.set('reboot_pending_by_action', _pAct);
      flow.set('reboot_timers', _tim);
      node.send([null, { topic:'actions/ack', payload: { actionId, status:'timeout', message:'reboot_all partial/timeout' } }]);
    }
  }, ACK_TIMEOUT_MS);

  flow.set('reboot_pending_by_action', pendingByAction);
  flow.set('reboot_pending_by_mac', pendingByMac);
  flow.set('reboot_timers', timers);

  const outMsgs = macs.map(m => ({
    topic: `esp32/control/${m}/reboot`,
    payload: { cmd:'Reboot', room_id:null, mac:m, actionId, ts:now }
  }));
  const ack = { payload:{ actionId, status:'accepted', message:`broadcast to ${macs.length} devices`} };
  return [outMsgs, ack];
}

// ========= Reboot Single =========
