const states = flow.get('deviceStates') || {};
const live = states?.[mac] || null;
const liveState = live?.lastCombinedState || null;
const livePingMs = Number.isFinite(live?.ping_ms) ? live.ping_ms : null;

// ‚ú® FIX: Sort by timestamp first, then by database ID to handle "flapping" status correctly ‚ú®
const list = rows
    .map(r => ({ id: r.id, status: String(r.status || '').toLowerCase(), ts: new Date(r.time).getTime() }))
    .filter(r => Number.isFinite(r.ts) && r.id)
    .sort((a, b) => {
        if (a.ts !== b.ts) {
            return a.ts - b.ts;
        }
        return a.id - b.id; // Secondary sort by ID ensures correct order for same-timestamp events
    });

// ====== Handle Case: No Historical Data ======
if (list.length === 0) {
    const seg = {
        status: liveState || 'offline',
        startTime: new Date(startMs).toISOString(),
        endTime: new Date(endMs).toISOString()
    };
    const onlineSec = (seg.status === 'online') ? (endMs - startMs) / 1000 : 0;
    const totalSec = (endMs - startMs) / 1000;
    msg.payload = {
        uptime_pct: totalSec > 0 ? +(onlineSec / totalSec * 100).toFixed(2) : 0,
        segments: [seg],
        authority: 'no_data',
        live: { state: liveState, ping_ms: livePingMs }
    };
    return msg;
}

// ====== New Uptime Calculation Logic ======
const segments = [];
let onlineMs = 0;

let cursor_ts = startMs;
let last_status = 'offline'; 

const pointsBefore = list.filter(p => p.ts < startMs);
if (pointsBefore.length > 0) {
    last_status = pointsBefore[pointsBefore.length - 1].status;
}

for (const point of list) {
    // This condition is important for correctly establishing the state at the beginning of the window,
    // especially when multiple events happen before the window starts.
    if (point.ts < cursor_ts) {
        last_status = point.status; // Update status for points before the window starts
        continue;
    }

    const seg_end = Math.min(point.ts, endMs);

    if (seg_end > cursor_ts) {
         segments.push({
            status: last_status,
            startTime: new Date(cursor_ts).toISOString(),
            endTime: new Date(seg_end).toISOString()
        });
        if (last_status === 'online') {
            onlineMs += (seg_end - cursor_ts);
        }
    }

    cursor_ts = point.ts;
    last_status = point.status;

    if (cursor_ts >= endMs) {
        break;
    }
}

if (cursor_ts < endMs) {
    segments.push({
        status: last_status,
        startTime: new Date(cursor_ts).toISOString(),
        endTime: new Date(endMs).toISOString()
    });
    if (last_status === 'online') {
        onlineMs += (endMs - cursor_ts);
    }
}

// ====== Final Calculation & Output ======
const totalMs = endMs - startMs;
const uptimePct = totalMs > 0 ? +(onlineMs / totalMs * 100).toFixed(2) : 0;

msg.payload = {
    uptime_pct: uptimePct,
    segments: segments,
    authority: 'db_only', 
    live: { state: liveState, ping_ms: livePingMs }
};

return msg;

// Function: eval_alert_v6.6
// ‡∏Ñ‡∏£‡∏ö‡∏ä‡∏∏‡∏î open / ack / recovery / close

// --- CONTEXTS ---
var states = flow.get('alert_states') || {};
var rules = global.get('effectiveRuleMap') || {};
var deviceMap = flow.get('device_map', 'file') || {};
var now = new Date();
var now_iso = now.toISOString();

function getLocalSqlTimestamp() {
    const d = new Date();
    const tzOffset = d.getTimezoneOffset() * 60000;
    const localTime = new Date(d.getTime() - tzOffset);
    return localTime.toISOString().slice(0, 19).replace('T', ' ');
}
var now_sql = getLocalSqlTimestamp();

// --- INPUT PAYLOAD ---
var p = msg.payload;
if (!p || !p.mac || !p.sensor_name || !p.metric || p.value === undefined) {
    node.error("Invalid payload", msg);
    return null;
}

var mac = p.mac;
var sensor = p.sensor_name;
var metric = p.metric;
var value = Number(p.value);
var dedup_key = `${mac}|${sensor}|${metric}`;

// --- HELPERS ---
function getEffectiveRule(mac, sensor, metric) {
    var sensorKey = `${mac}|${sensor}|${metric}`;
    if (rules[sensorKey]) return rules[sensorKey];
    var overallKey = `${mac}|overall|${metric}`;
    if (rules[overallKey]) return rules[overallKey];
    return rules[`system_${metric}`] || {};
}
function numOrNull(x) {
    return (x === undefined || x === null || x === '') ? null : Number(x);
}
function determineRawLevel(value, rule) {
    var crit_max = numOrNull(rule.crit_max);
    var crit_min = numOrNull(rule.crit_min);
    var warn_max = numOrNull(rule.warn_max);
    var warn_min = numOrNull(rule.warn_min);
    if (crit_max !== null && value > crit_max) return 'critical';
    if (crit_min !== null && value < crit_min) return 'critical';
    if (warn_max !== null && value > warn_max) return 'warning';
    if (warn_min !== null && value < warn_min) return 'warning';
    return 'normal';
}
function uuidv4() {
    return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function (c) {
        var r = Math.random() * 16 | 0, v = c == 'x' ? r : (r & 0x3 | 0x8);
        return v.toString(16);
    });
}
function getRoomName(mac) {
    for (const key in deviceMap) {
        if (key.startsWith('room') && deviceMap[key] === mac) {
            const roomLetter = key.slice(-1).toUpperCase();
            return deviceMap[`name${roomLetter}`] || mac;
        }
    }
    return mac;
}

var rule = getEffectiveRule(mac, sensor, metric);
if (!rule.id) {
    return null;
}

rule.warn_open_consec = rule.warn_open_consec ?? 2;
rule.crit_open_consec = rule.crit_open_consec ?? 1;
rule.clear_consec = rule.clear_consec ?? 2;

var state = states[dedup_key] || {
    status: 'closed',
    level: 'normal',
    warn_count: 0,
    crit_count: 0,
    clear_count: 0,
    event_id: null,
    ack_by: null,
    ack_note: null
};

var rawLevel = determineRawLevel(value, rule);

if (rawLevel === 'normal') {
    state.clear_count++;
    state.warn_count = 0;
    state.crit_count = 0;
} else if (rawLevel === 'warning') {
    state.warn_count++;
    state.crit_count = 0;
    state.clear_count = 0;
} else {
    state.crit_count++;
    state.warn_count = 0;
    state.clear_count = 0;
}

var outputs = [null, null, null];
var room = getRoomName(mac);

// A) RECOVERY
if ((state.status === 'open' || state.status === 'ack') && rawLevel === 'normal' && state.clear_count >= rule.clear_consec) {
    const eventToClose = state.event_id;
    const finalLevel = state.level;

    const dedupKey = `recovery_sent:${eventToClose}`;
    const lastSent = context.get(dedupKey);
    const nowMs = Date.now();
    if (lastSent && (nowMs - lastSent) < 60000) {
        return;
    }

    state.status = 'recovery';
    state.level = 'normal';

    const actorName = state.ack_by || 'System';
    const actorNote = state.ack_note || 'System Recovery';

    const updateSql = `
        UPDATE \`alert_events\`
        SET \`status\`='recovery', \`closed_at\`=?
        WHERE \`event_id\`=? 
          AND \`status\` IN ('open','ack')
          AND \`closed_at\` IS NULL;
    `;

    const logSql = `
        INSERT INTO \`alert_event_log\`
            (\`event_id\`, \`action\`, \`level\`, \`value\`, \`actor\`, \`ts\`, \`note\`, \`mac\`, \`sensor_name\`, \`metric\`)
        VALUES (?, 'recovery', ?, ?, ?, ?, ?, ?, ?, ?);
    `;

    outputs[0] = {
        topic: updateSql + logSql,
        payload: [now_sql, eventToClose,
            eventToClose, finalLevel, value, actorName, now_sql, actorNote, mac, sensor, metric]
    };

    outputs[2] = {
        payload: {
            type: 'recovery',
            event_id: eventToClose,
            dedup_key: dedup_key,
            mac,
            sensor_name: sensor,
            metric,
            value: value,
            ts: now_iso,
            status: 'recovery',
            room_name: room
        }
    };
    context.set(dedupKey, nowMs);
}

// B) OPEN NEW INCIDENT
else if (state.status === 'closed' && rawLevel !== 'normal') {
    var newLevel = null;
    if (rawLevel === 'critical' && state.crit_count >= rule.crit_open_consec) newLevel = 'critical';
    else if (rawLevel === 'warning' && state.warn_count >= rule.warn_open_consec) newLevel = 'warning';

    if (newLevel) {
        state.status = 'open';
        state.level = newLevel;
        state.event_id = uuidv4();
        state.ack_by = null;
        state.ack_note = null;

        outputs[0] = {
            topic: `
                INSERT INTO \`alert_events\` 
                    (\`event_id\`, \`dedup_key\`, \`mac\`, \`room_name\`, \`sensor_name\`, \`metric\`, \`level\`, \`trigger_value\`, \`status\`, \`rule_id\`, \`opened_at\`) 
                VALUES (?, ?, ?, ?, ?, ?, ?, ?, 'open', ?, ?);

                INSERT INTO \`alert_event_log\` 
                    (\`event_id\`, \`action\`, \`level\`, \`value\`, \`actor\`, \`ts\`, \`mac\`, \`sensor_name\`, \`metric\`) 
                VALUES (?, 'open', ?, ?, 'System', ?, ?, ?, ?);
            `,
            payload: [
                state.event_id, dedup_key, mac, room, sensor, metric, newLevel, value, rule.id, now_sql,
                state.event_id, newLevel, value, now_sql, mac, sensor, metric
            ]
        };
        outputs[1] = {
            payload: {
                type: 'open',
                level: newLevel,
                event_id: state.event_id,
                dedup_key: dedup_key,
                mac,
                sensor_name: sensor,
                metric,
                value,
                ts: now_iso,
                room_name: room,
                warn_min: rule.warn_min,
                warn_max: rule.warn_max
            }
        };
    }
}

// C) UPGRADE EXISTING INCIDENT
else if (state.status === 'open' && rawLevel !== 'normal') {
    const eventToUpdate = state.event_id;
    if (state.level === 'warning' && rawLevel === 'critical' && state.crit_count >= rule.crit_open_consec) {
        state.level = 'critical';
        outputs[0] = {
            topic: `
                UPDATE \`alert_events\` 
                SET \`level\`='critical', \`trigger_value\`=?, \`room_name\`=? 
                WHERE \`event_id\`=?;

                INSERT INTO \`alert_event_log\` 
                    (\`event_id\`, \`action\`, \`level\`, \`value\`, \`actor\`, \`ts\`, \`mac\`, \`sensor_name\`, \`metric\`) 
                VALUES (?, 'upgrade', 'critical', ?, 'System', ?, ?, ?, ?);
            `,
            payload: [
                value, room, eventToUpdate,
                eventToUpdate, value, now_sql, mac, sensor, metric
            ]
        };
        outputs[1] = {
            payload: {
                type: 'upgrade',
                level: 'critical',
                event_id: eventToUpdate,
                dedup_key: dedup_key,
                mac,
                sensor_name: sensor,
                metric,
                value,
                ts: now_iso,
                room_name: room,
                warn_min: rule.warn_min,
                warn_max: rule.warn_max
            }
        };
    } else {
        outputs[0] = {
            topic: `UPDATE \`alert_events\` SET \`last_updated\`=? WHERE \`event_id\`=?;`,
            payload: [now_sql, eventToUpdate]
        };
    }
}

// D) CLOSE (‡∏Å‡∏£‡∏ì‡∏µ‡∏ï‡πâ‡∏≠‡∏á‡∏õ‡∏¥‡∏î event ‡πÅ‡∏ö‡∏ö manual ‡∏´‡∏£‡∏∑‡∏≠ logic ‡∏≠‡∏∑‡πà‡∏ô)
else if (state.status === 'recovery') {
    const eventToClose = state.event_id;

    const actorName = state.ack_by || 'System';
    const actorNote = state.ack_note || 'System Close';

    const updateSql = `
        UPDATE \`alert_events\`
        SET \`status\`='closed', \`closed_at\`=?
        WHERE \`event_id\`=? AND \`status\`='recovery';
    `;

    const logSql = `
        INSERT INTO \`alert_event_log\`
            (\`event_id\`, \`action\`, \`level\`, \`value\`, \`actor\`, \`ts\`, \`note\`, \`mac\`, \`sensor_name\`, \`metric\`)
        VALUES (?, 'close', ?, ?, ?, ?, ?, ?, ?, ?);
    `;

    outputs[0] = {
        topic: updateSql + logSql,
        payload: [now_sql, eventToClose,
            eventToClose, state.level, value, actorName, now_sql, actorNote, mac, sensor, metric]
    };

    state.status = 'closed';
}

states[dedup_key] = state;
flow.set('alert_states', states);

if (outputs.some(o => o !== null)) {
    return outputs;
}
return null;

// Function: load_rules_to_global (FIXED to include sensor_connectivity)
const rules = msg.payload;
const deviceMap = flow.get('device_map', 'file') || {};

// ‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤‡∏Å‡∏é‡∏Ç‡∏≠‡∏á‡∏£‡∏∞‡∏ö‡∏ö (system-level rules) ‡∏ó‡∏±‡πâ‡∏á‡∏´‡∏°‡∏î‡∏ó‡∏µ‡πà‡πÄ‡∏£‡∏≤‡∏°‡∏µ
const systemTempRule = rules.find(r => r.scope === 'system' && r.metric === 'temp' && r.active) || {};
const systemHumRule = rules.find(r => r.scope === 'system' && r.metric === 'hum' && r.active) || {};
const systemConnRule = rules.find(r => r.scope === 'system' && r.metric === 'connectivity' && r.active) || {};
// --- ‚ú® ‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏ö‡∏£‡∏£‡∏ó‡∏±‡∏î‡∏ô‡∏µ‡πâ‡πÄ‡∏Ç‡πâ‡∏≤‡∏°‡∏≤ ‚ú® ---
const systemSensorConnRule = rules.find(r => r.scope === 'system' && r.metric === 'sensor_connectivity' && r.active) || {};
// --- ‚ú® ‡∏™‡∏¥‡πâ‡∏ô‡∏™‡∏∏‡∏î‡∏™‡πà‡∏ß‡∏ô‡∏ó‡∏µ‡πà‡πÄ‡∏û‡∏¥‡πà‡∏° ‚ú® ---

const roomRules = rules.filter(r => r.scope === 'room' && r.active);
const sensorRules = rules.filter(r => r.scope === 'sensor' && r.active);

const effectiveMap = {};

const inherit = (specific, room, system) => {
    const origins = {};
    const merged = {};
    const allKeys = new Set([...Object.keys(system), ...Object.keys(room), ...Object.keys(specific)]);

    for (const key of allKeys) {
        if (specific && specific[key] !== null && specific[key] !== undefined) {
            merged[key] = specific[key];
            origins[key] = 'sensor';
        } else if (room && room[key] !== null && room[key] !== undefined) {
            merged[key] = room[key];
            origins[key] = 'room';
        } else if (system && system[key] !== null && system[key] !== undefined) {
            merged[key] = system[key];
            origins[key] = 'system';
        }
    }
    merged.origins = origins;
    return merged;
};

const allMacs = Object.values(deviceMap).filter(val => typeof val === 'string' && val.includes(':'));

for (const mac of allMacs) {
    const sensorsForDevice = sensorRules.filter(r => r.mac === mac);

    const roomTempRule = roomRules.find(r => r.mac === mac && r.metric === 'temp') || {};
    const roomHumRule = roomRules.find(r => r.mac === mac && r.metric === 'hum') || {};

    const overallTempKey = `${mac}|overall|temp`;
    effectiveMap[overallTempKey] = inherit({}, roomTempRule, systemTempRule);

    const overallHumKey = `${mac}|overall|hum`;
    effectiveMap[overallHumKey] = inherit({}, roomHumRule, systemHumRule);

    for (const sensorRule of sensorsForDevice) {
        const key = `${mac}|${sensorRule.sensor_name}|${sensorRule.metric}`;
        if (sensorRule.metric === 'temp') {
            effectiveMap[key] = inherit(sensorRule, roomTempRule, systemTempRule);
        } else if (sensorRule.metric === 'hum') {
            effectiveMap[key] = inherit(sensorRule, roomHumRule, systemHumRule);
        }
    }
}

effectiveMap['system_temp'] = systemTempRule;
effectiveMap['system_hum'] = systemHumRule;
effectiveMap['system_connectivity'] = systemConnRule;
// --- ‚ú® ‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏ö‡∏£‡∏£‡∏ó‡∏±‡∏î‡∏ô‡∏µ‡πâ‡πÄ‡∏Ç‡πâ‡∏≤‡∏°‡∏≤ ‚ú® ---
effectiveMap['system_sensor_connectivity'] = systemSensorConnRule; 
// --- ‚ú® ‡∏™‡∏¥‡πâ‡∏ô‡∏™‡∏∏‡∏î‡∏™‡πà‡∏ß‡∏ô‡∏ó‡∏µ‡πà‡πÄ‡∏û‡∏¥‡πà‡∏° ‚ú® ---

global.set('effectiveRuleMap', effectiveMap);
node.status({ fill: 'green', shape: 'dot', text: `Loaded ${Object.keys(effectiveMap).length} effective rules` });

return null;

// Function: Query alert event lists (V8 - Removed pagination LIMIT)

const q = (msg.req && msg.req.query) ? msg.req.query : {};
const statusFilter = (q.status || 'open,ack').toLowerCase();
const startDate = q.start;
const endDate = q.end;

function toSqlDatetime(isoString) {
    if (!isoString) return null;
    try {
        const d = new Date(isoString);
        const tzOffset = d.getTimezoneOffset() * 60000;
        const localTime = new Date(d.getTime() - tzOffset);
        return localTime.toISOString().slice(0, 19).replace('T', ' ');
    } catch (e) {
        return null;
    }
}

let sql;
const params = [];

if (statusFilter.includes('closed') || statusFilter.includes('recovery')) {

    sql = `
    SELECT 
        e.event_id, e.mac, e.room_name, e.sensor_name, e.metric, e.level, e.trigger_value,
        l.action AS status,
        l.ts AS opened_at,
        l.ts AS display_time,
        l.actor AS ack_by,
        l.note AS ack_note,
        (SELECT sub.ts FROM alert_event_log sub WHERE sub.event_id = l.event_id AND sub.action = 'ack' ORDER BY sub.ts DESC LIMIT 1) as ack_at,
        (SELECT sub.ts FROM alert_event_log sub WHERE sub.event_id = l.event_id AND (sub.action = 'close' OR sub.action = 'recovery') ORDER BY sub.ts DESC LIMIT 1) as closed_at
    FROM 
        alert_event_log l
    LEFT JOIN 
        alert_events e ON l.event_id = e.event_id
    WHERE l.action IN ('close', 'recovery')
    `;

    const startSql = toSqlDatetime(startDate);
    const endSql = toSqlDatetime(endDate);

    if (startSql && endSql) {
        sql += ' AND l.ts BETWEEN ? AND ?';
        params.push(startSql, endSql);
    }

    if (q.mac) {
        sql += ' AND e.mac = ?';
        params.push(q.mac);
    }

    sql += ' ORDER BY l.ts DESC';

} else {
    // ‡πÇ‡∏Ñ‡πâ‡∏î‡πÄ‡∏î‡∏¥‡∏°‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡∏î‡∏∂‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏™‡∏ñ‡∏≤‡∏ô‡∏∞ Open ‡πÅ‡∏•‡∏∞ Ack ‡∏à‡∏≤‡∏Å `alert_events`
    sql = `
    SELECT 
        event_id, mac, room_name, sensor_name, metric, level, status,
        opened_at, ack_at, closed_at, ack_by, ack_note, trigger_value,
        CASE
            WHEN status = 'ack' THEN ack_at
            ELSE opened_at
        END AS display_time
    FROM alert_events
    `;

    const conditions = [];
    const statuses = statusFilter.split(',').map(s => s.trim()).filter(Boolean);
    if (statuses.length > 0) {
        conditions.push(`status IN (${statuses.map(() => '?').join(',')})`);
        params.push(...statuses);
    }
    if (q.mac) {
        conditions.push('mac = ?');
        params.push(q.mac);
    }
    if (conditions.length > 0) {
        sql += ' WHERE ' + conditions.join(' AND ');
    }
    sql += ' ORDER BY display_time DESC';
}

// ---- ‡∏™‡πà‡∏ß‡∏ô‡∏ó‡∏µ‡πà‡∏ñ‡∏π‡∏Å‡∏•‡∏ö‡∏≠‡∏≠‡∏Å‡πÑ‡∏õ ----
// const pageSize = parseInt(q.page_size, 10) || 50;
// const page = parseInt(q.page, 10) || 1;
// const offset = (page - 1) * pageSize;
// sql += ` LIMIT ${pageSize} OFFSET ${offset}`;
// -------------------------

msg.topic = sql;
msg.payload = params;

return msg;

// ‡∏£‡∏±‡∏ö query params ‡∏à‡∏≤‡∏Å‡∏´‡∏ô‡πâ‡∏≤‡πÄ‡∏ß‡πá‡∏ö
const q = msg.req.query;
const rules = global.get('effectiveRuleMap') || {};

// ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏ß‡πà‡∏≤‡∏°‡∏µ‡∏û‡∏≤‡∏£‡∏≤‡∏°‡∏¥‡πÄ‡∏ï‡∏≠‡∏£‡πå‡∏ó‡∏µ‡πà‡∏à‡∏≥‡πÄ‡∏õ‡πá‡∏ô‡∏Ñ‡∏£‡∏ö‡∏´‡∏£‡∏∑‡∏≠‡πÑ‡∏°‡πà
if (!q.mac || !q.sensor_name || !q.metric) {
    msg.statusCode = 400;
    msg.payload = { error: "mac, sensor_name, and metric are required query parameters" };
    return msg;
}

// ‡∏™‡∏£‡πâ‡∏≤‡∏á Key ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡πÅ‡∏ï‡πà‡∏•‡∏∞‡∏£‡∏∞‡∏î‡∏±‡∏ö (Sensor, Room, System) ‡πÉ‡∏´‡πâ‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á‡∏ï‡∏≤‡∏° metric ‡∏ó‡∏µ‡πà‡∏£‡πâ‡∏≠‡∏á‡∏Ç‡∏≠‡∏°‡∏≤
const sensorKey = `${q.mac}|${q.sensor_name}|${q.metric}`;
const roomKey = `${q.mac}|overall|${q.metric}`; // ‚ú® FIX: ‡∏ó‡∏≥‡πÉ‡∏´‡πâ‡πÄ‡∏õ‡πá‡∏ô Dynamic ‡∏ï‡∏≤‡∏° q.metric
const systemKey = `system_${q.metric}`;           // ‚ú® FIX: ‡∏ó‡∏≥‡πÉ‡∏´‡πâ‡πÄ‡∏õ‡πá‡∏ô Dynamic ‡∏ï‡∏≤‡∏° q.metric

// ‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤ Rule ‡∏ï‡∏≤‡∏°‡∏•‡∏≥‡∏î‡∏±‡∏ö‡∏Ñ‡∏ß‡∏≤‡∏°‡∏™‡∏≥‡∏Ñ‡∏±‡∏ç‡∏ó‡∏µ‡πà‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á: Sensor -> Room -> System
let effectiveRule = rules[sensorKey] || rules[roomKey] || rules[systemKey] || {};

msg.payload = effectiveRule;
return msg;

// Expects msg.payload to be an array of rule objects
const rulesToUpdate = Array.isArray(msg.payload) ? msg.payload : [msg.payload];

if (rulesToUpdate.length === 0) {
    msg.statusCode = 400;
    msg.payload = { ok: false, error: "Payload must be a non-empty array of rule objects." };
    return msg;
