    (243.12 * (LN(GREATEST(1e-6, t.avg_hum/100)) + (17.62*t.avg_temp)/(243.12+t.avg_temp)) ) / (17.62 - (LN(GREATEST(1e-6, t.avg_hum/100)) + (17.62*t.avg_temp)/(243.12+t.avg_temp))) AS dew_point
FROM dht_22 t
WHERE t.mac IN (${macs.map(() => '?').join(',')}) AND t.timestamp BETWEEN ? AND ?`;

// ‚ú® ‡πÅ‡∏Å‡πâ‡πÑ‡∏Ç‡∏ï‡∏£‡∏á‡∏ô‡∏µ‡πâ: ‡πÉ‡∏ä‡πâ‡πÄ‡∏ß‡∏•‡∏≤‡∏ó‡∏µ‡πà‡πÅ‡∏õ‡∏•‡∏á‡πÅ‡∏•‡πâ‡∏ß (fromSql, toSql)
const sensorParams = [...macs, fromSql, toSql];

if (sensors) {
  const sensorList = sensors.split(',');
  sensorSql += ` AND t.pin IN (${sensorList.map(() => '?').join(',')})`;
  sensorParams.push(...sensorList);
}
sensorSql += ` ORDER BY t.timestamp ASC;`;

msg.topic = sensorSql;
msg.payload = sensorParams;

return msg;

const alerts = msg.payload;
const sensors = msg.results.sensors;
const reportInfo = msg.reportInfo;

if (!reportInfo) {
