    } catch (e) { return isoString; }
};

// ‚ú® FIX: ‡πÅ‡∏Å‡πâ‡πÑ‡∏Ç‡∏Å‡∏≤‡∏£ map ‡∏ä‡∏∑‡πà‡∏≠ Metric ‡πÉ‡∏´‡πâ‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á‡∏ó‡∏±‡πâ‡∏á‡∏´‡∏°‡∏î ‚ú®
const metricMap = {
    temp: 'Temperature',
    hum: 'Humidity',
