        'Room Name': a.room_name,
        'Sensor Name': a.sensor_name,
        'Metric Raw': a.metric_raw,                  // << ‡πÉ‡∏™‡πà‡∏Ñ‡∏≠‡∏•‡∏±‡∏°‡∏ô‡πå‡∏î‡∏¥‡∏ö
        'Metric': metricMap[a.metric] || a.metric,   // << ‡∏ä‡∏∑‡πà‡∏≠‡∏≠‡πà‡∏≤‡∏ô‡∏á‡πà‡∏≤‡∏¢‡∏´‡∏•‡∏±‡∏á normalize
        'Level': a.level,
        'Value': a.trigger_value,
        'Status': a.status,
        'Ack By': a.acknowledged_by,
        'Ack Time': formatDate(a.acknowledged_time),
        'Closed Time': formatDate(a.closed_time)
    }))
};

return msg;

const { sensors, alerts } = msg.formattedData;
const { filename } = msg.reportInfo;

const csvSafe = (val) => {
    if (val === null || val === undefined) return '';
    let str = String(val);
    if (str.includes(',') || str.includes('"') || str.includes('\n')) {
        str = str.replace(/"/g, '""');
        return `"${str}"`;
    }
    return str;
};

let csvString = '';

const alertHeaders = ['Alert Time', 'Room Name', 'Sensor Name', 'Metric Raw', 'Metric', 'Level', 'Value', 'Status', 'Ack By', 'Ack Time', 'Closed Time'];
csvString += '\n### ALERT HISTORY ###\n';
csvString += alertHeaders.join(',') + '\n';
alerts.forEach(a => {
    const row = alertHeaders.map(h => csvSafe(a[h]));
    csvString += row.join(',') + '\n';
});
const sensorHeaders = ['Timestamp', 'Room Name', 'MAC Address', 'Sensor Pin', 'Sensor Name', 'Temperature (¬∞C)', 'Humidity (%)', 'Dew Point (¬∞C)'];
csvString += '### SENSOR DATA ###\n';
csvString += sensorHeaders.join(',') + '\n';
sensors.forEach(s => {
