return msg;

// üéØ GOAL: Close all 'recovery' events older than 7 days.
msg.topic = `
    UPDATE alert_events
    SET status = 'closed'
    WHERE status = 'recovery' AND closed_at < NOW() - INTERVAL 7 DAY;
`;
msg.payload = [];
return msg;

// Function Node: Adapt Sensor Status for Alert
// ‡∏£‡∏±‡∏ö‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏™‡∏ñ‡∏≤‡∏ô‡∏∞‡∏Ç‡∏≠‡∏á‡∏ó‡∏∏‡∏Å Sensor ‡πÅ‡∏•‡πâ‡∏ß‡∏™‡πà‡∏á‡∏ï‡πà‡∏≠‡πÑ‡∏õ‡πÉ‡∏´‡πâ Alert Engine ‡∏ó‡∏µ‡∏•‡∏∞‡∏ï‡∏±‡∏ß

const sensorStatuses = msg.payload; // msg.payload ‡πÄ‡∏õ‡πá‡∏ô Array [{mac, pin, status}, ...]
if (!Array.isArray(sensorStatuses)) {
    return null;
}

const pinMap = global.get('mac_pin_names') || {};

// ‡∏ß‡∏ô‡∏•‡∏π‡∏õ‡πÄ‡∏û‡∏∑‡πà‡∏≠‡∏™‡∏£‡πâ‡∏≤‡∏á message ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö Sensor ‡πÅ‡∏ï‡πà‡∏•‡∏∞‡∏ï‡∏±‡∏ß
sensorStatuses.forEach(sensor => {
    const isOffline = (sensor.status.toUpperCase() === 'OFFLINE');
    const alertValue = isOffline ? 0 : 1;

    // ‡∏î‡∏∂‡∏á‡∏ä‡∏∑‡πà‡∏≠‡∏ó‡∏µ‡πà‡∏ï‡∏±‡πâ‡∏á‡πÄ‡∏≠‡∏á‡∏Ç‡∏≠‡∏á Sensor (‡∏ñ‡πâ‡∏≤‡∏°‡∏µ)
    const devicePinMap = pinMap[sensor.mac] || {};
    const sensorName = devicePinMap[`name_pin_${sensor.pin}`] || `Pin ${sensor.pin}`;

    const newPayload = {
        mac: sensor.mac,
        sensor_name: sensorName, // ‡πÉ‡∏ä‡πâ‡∏ä‡∏∑‡πà‡∏≠‡∏ó‡∏µ‡πà‡∏ï‡∏±‡πâ‡∏á‡πÄ‡∏≠‡∏á
        metric: 'sensor_connectivity', // Metric ‡πÉ‡∏´‡∏°‡πà‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö Alert ‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó‡∏ô‡∏µ‡πâ
        value: alertValue, // 0 = offline, 1 = online
        ts: new Date().toISOString()
    };

    // ‡∏™‡πà‡∏á message ‡∏≠‡∏≠‡∏Å‡πÑ‡∏õ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö Sensor ‡∏ï‡∏±‡∏ß‡∏ô‡∏µ‡πâ
    node.send({ payload: newPayload });
});


// ‡πÑ‡∏°‡πà‡∏ï‡πâ‡∏≠‡∏á return ‡∏≠‡∏∞‡πÑ‡∏£ ‡πÄ‡∏û‡∏£‡∏≤‡∏∞‡πÄ‡∏£‡∏≤‡πÉ‡∏ä‡πâ node.send() ‡∏™‡πà‡∏á‡∏≠‡∏≠‡∏Å‡πÑ‡∏õ‡∏´‡∏°‡∏î‡πÅ‡∏•‡πâ‡∏ß
return null;

msg.results = {
    sensors: msg.payload
};

const { from, to, macs } = msg.reportInfo.params;

// ‚ú® FIX: ‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡πÅ‡∏õ‡∏•‡∏á‡πÄ‡∏ß‡∏•‡∏≤ Local ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö Database ‚ú®
function toSqlLocal(isoString) {
    if (!isoString) return null;
    try {
        const d = new Date(isoString);
        // ‡∏Ñ‡∏≥‡∏ô‡∏ß‡∏ì Offset ‡∏Ç‡∏≠‡∏á Timezone ‡πÄ‡∏û‡∏∑‡πà‡∏≠‡πÉ‡∏´‡πâ‡πÑ‡∏î‡πâ‡πÄ‡∏ß‡∏•‡∏≤ Local ‡∏ó‡∏µ‡πà‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á
        const tzOffset = d.getTimezoneOffset() * 60000;
        const localTime = new Date(d.getTime() - tzOffset);
        // ‡πÅ‡∏õ‡∏•‡∏á‡πÄ‡∏õ‡πá‡∏ô‡∏ü‡∏≠‡∏£‡πå‡πÅ‡∏°‡∏ï 'YYYY-MM-DD HH:MM:SS' ‡∏ó‡∏µ‡πà Database ‡πÄ‡∏Ç‡πâ‡∏≤‡πÉ‡∏à
        return localTime.toISOString().slice(0, 19).replace('T', ' ');
    } catch (e) {
        return isoString; // ‡∏ñ‡πâ‡∏≤‡πÅ‡∏õ‡∏•‡∏á‡πÑ‡∏°‡πà‡πÑ‡∏î‡πâ ‡πÉ‡∏´‡πâ‡πÉ‡∏ä‡πâ‡∏Ñ‡πà‡∏≤‡πÄ‡∏î‡∏¥‡∏°
    }
}

// ‡πÅ‡∏õ‡∏•‡∏á‡πÄ‡∏ß‡∏•‡∏≤‡∏ó‡∏µ‡πà‡∏£‡∏±‡∏ö‡∏°‡∏≤‡∏à‡∏≤‡∏Å UI ‡πÉ‡∏´‡πâ‡πÄ‡∏õ‡πá‡∏ô Local Time ‡∏Ç‡∏≠‡∏á Database
const fromSql = toSqlLocal(from);
const toSql = toSqlLocal(to);


// ‚ú® SQL Query ‡∏ó‡∏µ‡πà‡∏£‡∏ß‡∏° Logic ‡∏Å‡∏≤‡∏£‡πÅ‡∏õ‡∏•‡∏á Metric ‡πÅ‡∏•‡∏∞‡πÄ‡∏á‡∏∑‡πà‡∏≠‡∏ô‡πÑ‡∏Ç‡πÄ‡∏ß‡∏•‡∏≤‡∏ó‡∏µ‡πà‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á ‚ú®
const alertSql = `
SELECT
    opened_at AS alert_time,
    mac,
    room_name,
    sensor_name,
    metric AS metric_raw,
    -- 'metric' ‡∏Ç‡∏≠‡∏á sensor offline ‡∏à‡∏∞‡∏ñ‡∏π‡∏Å‡πÄ‡∏Å‡πá‡∏ö‡πÄ‡∏õ‡πá‡∏ô 'sensor_connectivity' ‡∏≠‡∏¢‡∏π‡πà‡πÅ‡∏•‡πâ‡∏ß
    -- ‡∏î‡∏±‡∏á‡∏ô‡∏±‡πâ‡∏ô‡πÄ‡∏£‡∏≤‡πÑ‡∏°‡πà‡∏à‡∏≥‡πÄ‡∏õ‡πá‡∏ô‡∏ï‡πâ‡∏≠‡∏á‡πÉ‡∏ä‡πâ CASE statement ‡πÉ‡∏ô‡∏™‡πà‡∏ß‡∏ô‡∏ô‡∏µ‡πâ
    metric,
    level,
    trigger_value,
    status,
    ack_by AS acknowledged_by,
    ack_at AS acknowledged_time,
    closed_at AS closed_time
FROM alert_events
WHERE
    mac IN (${macs.map(() => '?').join(',')})
    AND opened_at <= ?
    AND (closed_at IS NULL OR closed_at >= ?)
ORDER BY opened_at ASC;`;

// ‡πÉ‡∏ä‡πâ‡πÄ‡∏ß‡∏•‡∏≤‡∏ó‡∏µ‡πà‡πÅ‡∏õ‡∏•‡∏á‡πÅ‡∏•‡πâ‡∏ß (toSql, fromSql) ‡πÉ‡∏ô‡∏Å‡∏≤‡∏£‡∏™‡πà‡∏á Query
const alertParams = [...macs, toSql, fromSql];

msg.topic = alertSql;
msg.payload = alertParams;

return msg;

function toLocalSqlDate(d) {
  d = d || new Date();
  const local = new Date(d.getTime() - d.getTimezoneOffset() * 60000);
  return local.toISOString().slice(0,19).replace('T',' ');
}
let data = msg.payload;
let time = data.event_time
  ? toLocalSqlDate(new Date(data.event_time))
  : toLocalSqlDate();

msg.topic = `
  INSERT INTO esp_status_log
    (mac, room, status, time)
  VALUES
    (
      '${data.mac}',
      '${data.room.replace(/'/g,"''")}',
      '${data.status}',
      '${time}'
    );
`.replace(/\s+/g,' ');

return msg;

// --- ‡∏™‡πà‡∏ß‡∏ô‡∏ï‡∏±‡πâ‡∏á‡∏Ñ‡πà‡∏≤‡∏≠‡∏∏‡∏õ‡∏Å‡∏£‡∏ì‡πå ---
const deviceConfig = {
    '94:B9:7E:CE:01:EF': { ip: '192.168.42.30', room: 'Server Room 1' }
};

// --- ‡∏ï‡∏±‡πâ‡∏á‡∏Ñ‡πà‡∏≤‡∏Å‡∏≤‡∏£‡∏´‡∏ô‡πà‡∏ß‡∏á‡πÄ‡∏ß‡∏•‡∏≤ ---
const OFFLINE_GRACE_PERIOD_MS = 60000;
const MQTT_TIMEOUT_MS = 21000;

// --- ‡∏ï‡∏±‡∏ß‡πÅ‡∏õ‡∏£ Global ---
let states = flow.get('deviceStates') || {};
const ipToMacMap = {};
for (const mac in deviceConfig) {
    ipToMacMap[deviceConfig[mac].ip] = mac;
}

// --- ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡∏ä‡πà‡∏ß‡∏¢‡πÄ‡∏´‡∏•‡∏∑‡∏≠ ---
function now() { return Date.now(); }
function normalizeMac(s) {
    if (!s) return null;
    s = String(s).toUpperCase().replace(/[^0-9A-F]/g, '');
    if (s.length !== 12) return null;
    return s.match(/.{2}/g).join(':');
}

// --- Logic ‡∏´‡∏•‡∏±‡∏Å ---
let mac = null;
let sourceType = null;

if (msg.topic && ipToMacMap[msg.topic]) {
    sourceType = "Ping";
    mac = ipToMacMap[msg.topic];
} else if (msg.topic && msg.topic.startsWith('esp32/')) {
    sourceType = "MQTT";
    mac = normalizeMac(msg.payload && msg.payload.mac) || normalizeMac(msg.topic.split('/')[1]);
}

if (!mac || !deviceConfig[mac]) {
    return null;
}

if (!states[mac]) {
    states[mac] = {
        pingOk: false, mqttOk: false, lastCombinedState: 'offline',
        ping_ms: null, lastMqttTs: 0, offlineSince: null
    };
}

const oldState = states[mac].lastCombinedState;
const deviceState = states[mac];

if (sourceType === "MQTT") {
    deviceState.mqttOk = (msg.payload && msg.payload.status || '').toString().toLowerCase() === 'online';
    deviceState.lastMqttTs = now();
} else { // sourceType === "Ping"
    deviceState.pingOk = (msg.payload !== false);
    deviceState.ping_ms = (msg.payload !== false) ? Number(msg.payload) : deviceState.ping_ms;
}

if ((now() - deviceState.lastMqttTs) > MQTT_TIMEOUT_MS) {
    deviceState.mqttOk = false;
}

const potentialState = (deviceState.pingOk && deviceState.mqttOk) ? 'online' : 'offline';
let statusMsg = null;
let configRequestMsg = null; // ‚ú® Message ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡∏Ç‡∏≠ Config

if (potentialState === 'online') {
    deviceState.offlineSince = null;
    if (oldState === 'offline') {
        deviceState.lastCombinedState = 'online';
        statusMsg = { // Message ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö Output 1
            payload: {
                mac: mac, room: deviceConfig[mac].room, status: 'online',
                ping_ms: deviceState.ping_ms, event_time: new Date().toISOString(), pin: "OVERALL"
            }
        };
        // ‚ú® ‡πÄ‡∏°‡∏∑‡πà‡∏≠‡∏Å‡∏•‡∏±‡∏ö‡∏°‡∏≤ Online ‡πÉ‡∏´‡πâ‡∏™‡∏£‡πâ‡∏≤‡∏á message ‡πÄ‡∏û‡∏∑‡πà‡∏≠‡πÑ‡∏õ‡∏Ç‡∏≠ Config ‡∏î‡πâ‡∏ß‡∏¢
        configRequestMsg = { // Message ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö Output 2
            payload: {
                command: "get_config",
                mac: mac,
                // ‡πÉ‡∏™‡πà requestId ‡πÄ‡∏û‡∏∑‡πà‡∏≠‡πÉ‡∏´‡πâ‡∏ï‡∏¥‡∏î‡∏ï‡∏≤‡∏°‡πÑ‡∏î‡πâ‡∏á‡πà‡∏≤‡∏¢ (optional)
                requestId: `auto-get-${Date.now()}`
            }
        };
    }
} else { // potentialState === 'offline'
    if (oldState === 'online') {
        if (deviceState.offlineSince === null) {
            deviceState.offlineSince = now();
        }
    }
    if (deviceState.offlineSince && (now() - deviceState.offlineSince > OFFLINE_GRACE_PERIOD_MS) && oldState === 'online') {
        deviceState.lastCombinedState = 'offline';
        statusMsg = {
            payload: {
                mac: mac, room: deviceConfig[mac].room, status: 'offline',
                ping_ms: deviceState.ping_ms, event_time: new Date().toISOString(), pin: "OVERALL"
            }
        };
    }
}

flow.set('deviceStates', states);

// ‚ú® ‡∏™‡πà‡∏á message ‡∏≠‡∏≠‡∏Å 2 ‡∏Ç‡∏≤: [output1, output2]
return [statusMsg, configRequestMsg];

// ‡∏î‡∏∂‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ó‡∏µ‡πà‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡πÑ‡∏ß‡πâ ‡∏ñ‡πâ‡∏≤‡πÑ‡∏°‡πà‡∏°‡∏µ ‡πÉ‡∏´‡πâ‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÄ‡∏£‡∏¥‡πà‡∏°‡∏ï‡πâ‡∏ô‡πÉ‡∏ô "‡πÇ‡∏Ñ‡∏£‡∏á‡∏™‡∏£‡πâ‡∏≤‡∏á‡πÉ‡∏´‡∏°‡πà" ‡∏ó‡∏µ‡πà‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á
const settings = flow.get('notificationSettings') || {
    recipients: {
        email: [],
        telegram: []
    },
    schedule: {
        weekdays: [true, true, true, true, true, true, true],
        configs: [{ sendAt: "08:00", rangeStart: "17:00", rangeEnd: "08:00" }]
    }
};

msg.payload = settings;
return msg;

// msg.payload ‡∏Ñ‡∏∑‡∏≠ JSON ‡∏ó‡∏µ‡πà UI ‡∏™‡πà‡∏á‡∏°‡∏≤
let payload = msg.payload;

// --- 1. ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö Recipients ---
if (!payload.recipients) {
    payload.recipients = { email: [], telegram: [] };
}
if (!Array.isArray(payload.recipients.email)) {
    payload.recipients.email = [];
}
if (!Array.isArray(payload.recipients.telegram)) {
    payload.recipients.telegram = [];
}

// --- 2. ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö Schedule (‡πÇ‡∏Ñ‡∏£‡∏á‡∏™‡∏£‡πâ‡∏≤‡∏á‡πÉ‡∏´‡∏°‡πà) ---
const defaults = {
    weekdays: [true, true, true, true, true, true, true],
    configs: [{ sendAt: "08:00", rangeStart: "17:00", rangeEnd: "08:00" }]
};

let schedule = payload.schedule;
let isScheduleInvalid = true;

// ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏ß‡πà‡∏≤ schedule.configs ‡πÄ‡∏õ‡πá‡∏ô Array ‡∏ó‡∏µ‡πà‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á‡∏´‡∏£‡∏∑‡∏≠‡πÑ‡∏°‡πà
if (schedule && Array.isArray(schedule.weekdays) && schedule.weekdays.length === 7 && Array.isArray(schedule.configs)) {
    // ‡∏ñ‡πâ‡∏≤‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á (‡∏≠‡∏≤‡∏à‡∏à‡∏∞‡πÄ‡∏õ‡πá‡∏ô Array ‡∏ß‡πà‡∏≤‡∏á‡∏Å‡πá‡πÑ‡∏î‡πâ ‡∏ñ‡πâ‡∏≤‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ‡∏•‡∏ö‡∏´‡∏°‡∏î)
    isScheduleInvalid = false;
}

// ‡∏ñ‡πâ‡∏≤‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÑ‡∏°‡πà‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á ‡πÉ‡∏´‡πâ‡πÉ‡∏ä‡πâ‡∏Ñ‡πà‡∏≤ Default
if (isScheduleInvalid) {
    node.warn("Received empty or invalid schedule. Applying default settings.");
    payload.schedule = defaults;
}

msg.payload = payload;
return msg;

// Update Cron Schedule (patched)
// ‡∏™‡∏£‡πâ‡∏≤‡∏á/‡∏•‡∏ö schedule ‡πÉ‡∏ô cronplus ‡πÉ‡∏´‡πâ‡∏¢‡∏¥‡∏á‡∏ï‡∏≤‡∏° UI settings
const DEFAULT_SETTINGS = {
  recipients: { email: [], telegram: [] },
  schedule: {
    weekdays: [true,true,true,true,true,true,true],
    configs: [{ sendAt: "08:00", rangeStart: "17:00", rangeEnd: "08:00" }]
  }
};

// ‡πÇ‡∏´‡∏•‡∏î‡∏Ñ‡πà‡∏≤ config
let settings = flow.get("notificationSettings");
if (!settings || !settings.schedule || !Array.isArray(settings.schedule.configs)) {
  node.warn("Notification settings missing or invalid. Seeding defaults.");
  settings = DEFAULT_SETTINGS;
  flow.set("notificationSettings", settings);
}

// ‡∏ß‡∏±‡∏ô‡πÉ‡∏ô‡∏™‡∏±‡∏õ‡∏î‡∏≤‡∏´‡πå (0=Sun..6=Sat)
const weekdays = Array.isArray(settings.schedule.weekdays) ? settings.schedule.weekdays : [];
const selectedDOW = weekdays
  .map((isOn, idx) => isOn ? idx : -1)
  .filter(v => v !== -1);

// ‡πÄ‡∏Ñ‡∏•‡∏µ‡∏¢‡∏£‡πå‡∏á‡∏≤‡∏ô‡πÄ‡∏Å‡πà‡∏≤‡∏Å‡πà‡∏≠‡∏ô
const removeAllMsg = { payload: { command: "remove-all" } };

// ‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏ß‡∏±‡∏ô‡∏´‡∏£‡∏∑‡∏≠‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏ï‡∏≤‡∏£‡∏≤‡∏á‡πÄ‡∏ß‡∏•‡∏≤ ‚Üí ‡∏•‡∏ö‡πÅ‡∏•‡πâ‡∏ß‡∏à‡∏ö
if (selectedDOW.length === 0 || !Array.isArray(settings.schedule.configs) || settings.schedule.configs.length === 0) {
  node.warn("No days or schedules selected. Removing all cron jobs.");
  return [[removeAllMsg], null];
}

// ‡∏ó‡∏≥ string DOW
const daysOfWeekStr = selectedDOW.join(',');

// timeZone ‡∏ó‡∏µ‡πà‡∏à‡∏∞‡πÉ‡∏ä‡πâ‡∏ó‡∏±‡πâ‡∏á‡πÉ‡∏ô node ‡πÅ‡∏•‡∏∞‡πÉ‡∏ô‡∏Ñ‡∏≥‡∏™‡∏±‡πà‡∏á
const TZ = "Asia/Bangkok";

// ‡∏™‡∏£‡πâ‡∏≤‡∏á job ‡πÉ‡∏´‡∏°‡πà‡∏ï‡∏≤‡∏°‡πÅ‡∏ï‡πà‡∏•‡∏∞ config
const crypto = (s)=> {
  // ‡πÅ‡∏Æ‡∏ä‡∏à‡∏¥‡πã‡∏ß‡∏Å‡∏±‡∏ô‡∏ä‡∏∑‡πà‡∏≠‡∏ä‡∏ô (‡πÑ‡∏°‡πà‡∏ï‡πâ‡∏≠‡∏á‡πÄ‡∏≠‡∏≤‡∏à‡∏£‡∏¥‡∏á‡∏à‡∏±‡∏á)
  let h = 0; for (let i=0;i<s.length;i++) h = ((h<<5)-h) + s.charCodeAt(i) | 0;
  return Math.abs(h).toString(36).slice(0,5);
};

const addMsgs = settings.schedule.configs
  .filter(cfg => cfg && typeof cfg.sendAt === "string" && cfg.sendAt.includes(":"))
  .map((cfg) => {
    const [hourStr, minuteStr] = cfg.sendAt.split(":").map(s => String(s).trim());
    const hour = String(parseInt(hourStr, 10));
    const minute = String(parseInt(minuteStr, 10));

    // cron 6 ‡∏ü‡∏¥‡∏•‡∏î‡πå: sec min hour dom month dow
    const expression = `0 ${minute} ${hour} * * ${daysOfWeekStr}`;

    // ‡∏ó‡∏≥‡∏ä‡∏∑‡πà‡∏≠ job ‡πÉ‡∏´‡πâ‡∏Ñ‡∏á‡∏ó‡∏µ‡πà‡πÅ‡∏ï‡πà‡πÑ‡∏°‡πà‡∏ä‡∏ô‡∏Å‡∏±‡∏ô‡∏á‡πà‡∏≤‡∏¢
    const sig = `${hour}:${minute}|${daysOfWeekStr}|${cfg.rangeStart||''}-${cfg.rangeEnd||''}`;
    const name = `digest-${crypto(sig)}`;

    return {
      payload: {
        command: "add",
        name,
        expressionType: "cron",
        expression,
        timeZone: TZ,
        // payload ‡∏ó‡∏µ‡πà‡∏õ‡∏•‡πà‡∏≠‡∏¢‡∏ï‡∏≠‡∏ô‡∏ñ‡∏∂‡∏á‡πÄ‡∏ß‡∏•‡∏≤ ‡πÉ‡∏´‡πâ‡∏û‡∏Å cfg ‡∏≠‡∏≠‡∏Å‡πÑ‡∏õ‡∏î‡πâ‡∏ß‡∏¢
        payload: {
          kind: "daily-digest-trigger",
          schedule: { ...cfg, daysOfWeekStr, timeZone: TZ }
        },
        payloadType: "json"
      }
    };
  });

// ‡∏™‡πà‡∏á‡∏≠‡∏≠‡∏Å: output1 = remove-all, output2 = add ‡∏ó‡∏±‡πâ‡∏á‡∏´‡∏°‡∏î
return [[removeAllMsg], addMsgs];

// Function: eval_alert_v6.2 (Passes rule thresholds to output)

// --- CONTEXTS ---
var states = flow.get('alert_states') || {};
var rules = global.get('effectiveRuleMap') || {};
var deviceMap = flow.get('device_map', 'file') || {};
var now = new Date();
var now_iso = now.toISOString();

function getLocalSqlTimestamp() {
    const d = new Date();
    const tzOffset = d.getTimezoneOffset() * 60000;
    const localTime = new Date(d.getTime() - tzOffset);
    return localTime.toISOString().slice(0, 19).replace('T', ' ');
}
var now_sql = getLocalSqlTimestamp();

// --- INPUT PAYLOAD ---
var p = msg.payload;
if (!p || !p.mac || !p.sensor_name || !p.metric || p.value === undefined) {
    node.error("Invalid payload", msg);
    return null;
}

var mac = p.mac;
var sensor = p.sensor_name;
var metric = p.metric;
var value = Number(p.value);
var dedup_key = `${mac}|${sensor}|${metric}`;

// --- HELPERS ---
function getEffectiveRule(mac, sensor, metric) {
    var sensorKey = `${mac}|${sensor}|${metric}`;
    if (rules[sensorKey]) return rules[sensorKey];
    var overallKey = `${mac}|overall|${metric}`;
    if (rules[overallKey]) return rules[overallKey];
    return rules[`system_${metric}`] || {};
}
function numOrNull(x) {
    return (x === undefined || x === null || x === '') ? null : Number(x);
}
function determineRawLevel(value, rule) {
    var crit_max = numOrNull(rule.crit_max);
    var crit_min = numOrNull(rule.crit_min);
    var warn_max = numOrNull(rule.warn_max);
    var warn_min = numOrNull(rule.warn_min);
    if (crit_max !== null && value > crit_max) return 'critical';
    if (crit_min !== null && value < crit_min) return 'critical';
    if (warn_max !== null && value > warn_max) return 'warning';
    if (warn_min !== null && value < warn_min) return 'warning';
    return 'normal';
}
function uuidv4() {
    return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function (c) {
        var r = Math.random() * 16 | 0, v = c == 'x' ? r : (r & 0x3 | 0x8);
        return v.toString(16);
    });
}
function getRoomName(mac) {
    for (const key in deviceMap) {
        if (key.startsWith('room') && deviceMap[key] === mac) {
            const roomLetter = key.slice(-1).toUpperCase();
            return deviceMap[`name${roomLetter}`] || mac;
        }
    }
    return mac;
}

var rule = getEffectiveRule(mac, sensor, metric);
if (!rule.id) {
    return null;
}


rule.warn_open_consec = rule.warn_open_consec ?? 2;
rule.crit_open_consec = rule.crit_open_consec ?? 1;
rule.clear_consec = rule.clear_consec ?? 2;

var state = states[dedup_key] || {
    status: 'closed',
    level: 'normal',
    warn_count: 0,
    crit_count: 0,
    clear_count: 0,
    event_id: null,
};


var rawLevel = determineRawLevel(value, rule);

if (rawLevel === 'normal') {
    state.clear_count++;
    state.warn_count = 0;
    state.crit_count = 0;
} else if (rawLevel === 'warning') {
    state.warn_count++;
    state.crit_count = 0;
    state.clear_count = 0;
} else {
    state.crit_count++;
    state.warn_count = 0;
    state.clear_count = 0;
}

var outputs = [null, null, null];
var room = getRoomName(mac);

// A) RECOVERY: Close an existing open or acknowledged incident (idempotent)
if ((state.status === 'open' || state.status === 'ack') && rawLevel === 'normal' && state.clear_count >= rule.clear_consec) {
    const eventToClose = state.event_id;
    const originalStatus = state.status;
    const finalLevel = state.level;

    // ‡∏Å‡∏±‡∏ô‡∏¢‡∏¥‡∏á‡∏ã‡πâ‡∏≥‡∏ù‡∏±‡πà‡∏á‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡∏î‡πâ‡∏ß‡∏¢ dedup key ‡πÉ‡∏ô context (60s)
    const dedupKey = `recovery_sent:${eventToClose}`;
    const lastSent = context.get(dedupKey);
    const nowMs = Date.now();
    if (lastSent && (nowMs - lastSent) < 60000) {
        // ‡∏†‡∏≤‡∏¢‡πÉ‡∏ô 60s ‡πÑ‡∏°‡πà‡∏ï‡πâ‡∏≠‡∏á‡∏ó‡∏≥‡∏≠‡∏∞‡πÑ‡∏£
        return;
    }

    // ‡∏≠‡∏±‡∏õ‡πÄ‡∏î‡∏ï‡∏™‡∏ñ‡∏≤‡∏ô‡∏∞‡πÉ‡∏ô state (‡πÉ‡∏ô‡∏´‡∏ô‡πà‡∏ß‡∏¢‡∏Ñ‡∏ß‡∏≤‡∏°‡∏à‡∏≥‡∏Ç‡∏≠‡∏á flow ‡∏ô‡∏µ‡πâ)
    state.status = 'recovery';
    state.level = 'normal';

    const logNote = (originalStatus === 'ack') ? 'System Recovery (post-ack)' : 'System Recovery';

    // ‚úÖ UPDATE ‡πÄ‡∏â‡∏û‡∏≤‡∏∞‡πÄ‡∏Ñ‡∏™‡∏ó‡∏µ‡πà‡∏¢‡∏±‡∏á‡πÑ‡∏°‡πà‡∏õ‡∏¥‡∏î (closed_at IS NULL) ‡πÅ‡∏•‡∏∞‡πÑ‡∏°‡πà‡∏ó‡∏±‡∏ö ack ‡πÄ‡∏î‡∏¥‡∏°
    const updateSql = `
        UPDATE \`alert_events\`
        SET
            \`status\`   = 'recovery',
            \`closed_at\`= ?,
            \`ack_by\`   = CASE WHEN COALESCE(\`ack_by\`, '')   = '' THEN 'System'  ELSE \`ack_by\`   END,
            \`ack_note\` = CASE WHEN COALESCE(\`ack_note\`, '') = '' THEN ?         ELSE \`ack_note\` END
        WHERE \`event_id\` = ? 
          AND \`status\` IN ('open','ack')
          AND \`closed_at\` IS NULL;
    `;

    // ‚úÖ ‡∏ú‡∏π‡∏Å‡∏Å‡∏≤‡∏£ INSERT log ‡∏Å‡∏±‡∏ö‡∏ú‡∏•‡∏•‡∏±‡∏û‡∏ò‡πå‡∏Ç‡∏≠‡∏á UPDATE ‡πÄ‡∏ó‡πà‡∏≤‡∏ô‡∏±‡πâ‡∏ô
    // - ‡∏ñ‡πâ‡∏≤ UPDATE ‡πÑ‡∏°‡πà‡πÑ‡∏î‡πâ‡πÅ‡∏Å‡πâ‡∏™‡∏±‡∏Å‡πÅ‡∏ñ‡∏ß (@rc = 0) ‡∏à‡∏∞‡πÑ‡∏°‡πà INSERT log
    const guardedLogSql = `
        SET @rc = ROW_COUNT();
        INSERT INTO \`alert_event_log\`
            (\`event_id\`, \`action\`, \`level\`, \`value\`, \`actor\`, \`ts\`, \`note\`, \`mac\`, \`sensor_name\`, \`metric\`)
        SELECT ?, 'close', ?, ?, 'System', ?, ?, ?, ?, ?
        WHERE @rc > 0;
    `;

    const updatePayload = [now_sql, logNote, eventToClose];
    const logPayload = [eventToClose, finalLevel, value, now_sql, logNote, mac, sensor, metric];

    // ‡∏™‡πà‡∏á‡πÑ‡∏õ‡∏¢‡∏±‡∏á MySQL (‡∏ï‡πâ‡∏≠‡∏á‡πÄ‡∏õ‡∏¥‡∏î Allow multiple statements)
    outputs[0] = {
        topic: updateSql + guardedLogSql,
        payload: [...updatePayload, ...logPayload]
    };

    // ‡∏™‡πà‡∏á notification ‡πÄ‡∏â‡∏û‡∏≤‡∏∞‡∏Ñ‡∏£‡∏±‡πâ‡∏á‡πÅ‡∏£‡∏Å ‡πÅ‡∏•‡πâ‡∏ß‡∏ï‡∏±‡πâ‡∏á‡∏ò‡∏á‡∏Å‡∏±‡∏ô‡∏ã‡πâ‡∏≥ 60s
    outputs[2] = {
        payload: {
            type: 'recovery',
            event_id: eventToClose,
            dedup_key: dedup_key,
            mac,
            sensor_name: sensor,
            metric,
            value: value,
            ts: now_iso,
            status: 'recovery',
            room_name: room
        }
    };
    context.set(dedupKey, nowMs);
}



// B) OPEN NEW INCIDENT: Always a new row
else if (state.status === 'closed' && rawLevel !== 'normal') {
    var newLevel = null;
    if (rawLevel === 'critical' && state.crit_count >= rule.crit_open_consec) newLevel = 'critical';
    else if (rawLevel === 'warning' && state.warn_count >= rule.warn_open_consec) newLevel = 'warning';

    if (newLevel) {
        state.status = 'open';
        state.level = newLevel;
        state.event_id = uuidv4();

        outputs[0] = {
            topic: `
                INSERT INTO \`alert_events\` 
                    (\`event_id\`, \`dedup_key\`, \`mac\`, \`room_name\`, \`sensor_name\`, \`metric\`, \`level\`, \`trigger_value\`, \`status\`, \`rule_id\`, \`opened_at\`) 
                VALUES (?, ?, ?, ?, ?, ?, ?, ?, 'open', ?, ?);

                INSERT INTO \`alert_event_log\` 
                    (\`event_id\`, \`action\`, \`level\`, \`value\`, \`actor\`, \`ts\`, \`mac\`, \`sensor_name\`, \`metric\`) 
                VALUES (?, 'open', ?, ?, 'System', ?, ?, ?, ?);
            `, // ‚ú® ‡∏à‡∏∏‡∏î‡∏ó‡∏µ‡πà 3
            payload: [
                state.event_id, dedup_key, mac, room, sensor, metric, newLevel, value, rule.id, now_sql,
                state.event_id, newLevel, value, now_sql, mac, sensor, metric
            ]
        };
        // Pass essential info for notification
        outputs[1] = {
            payload: {
                type: 'open',
                level: newLevel,
                event_id: state.event_id,
                dedup_key: dedup_key,
                mac,
                sensor_name: sensor,
                metric,
                value,
                ts: now_iso,
                room_name: room, // <-- ADDED
                warn_min: rule.warn_min, // <-- ADDED
                warn_max: rule.warn_max  // <-- ADDED
            }
        };
    }
}

// C) UPDATE EXISTING OPEN INCIDENT (Upgrade or Heartbeat)
else if (state.status === 'open' && rawLevel !== 'normal') {
    const eventToUpdate = state.event_id;
    if (state.level === 'warning' && rawLevel === 'critical' && state.crit_count >= rule.crit_open_consec) {
        state.level = 'critical';
        outputs[0] = {
            topic: `
                UPDATE \`alert_events\` 
                SET \`level\`='critical', \`trigger_value\`=?, \`room_name\`=? 
                WHERE \`event_id\`=?;

                INSERT INTO \`alert_event_log\` (\`event_id\`, \`action\`, \`level\`, \`value\`, \`actor\`, \`ts\`, \`mac\`, \`sensor_name\`, \`metric\`) 
                VALUES (?, 'upgrade', 'critical', ?, 'System', ?, ?, ?, ?);
            `, // ‚ú® ‡∏à‡∏∏‡∏î‡∏ó‡∏µ‡πà 4
            payload: [
                value, room, eventToUpdate,
                eventToUpdate, value, now_sql, mac, sensor, metric
            ]
        };
        // Pass essential info for notification
        outputs[1] = {
            payload: {
                type: 'upgrade',
                level: 'critical',
                event_id: eventToUpdate,
                dedup_key: dedup_key,
                mac,
                sensor_name: sensor,
                metric,
                value,
                ts: now_iso,
                room_name: room, // <-- ADDED
                warn_min: rule.warn_min, // <-- ADDED
                warn_max: rule.warn_max  // <-- ADDED
            }
        };
    } else {
        // Just a heartbeat update
        outputs[0] = {
            topic: `UPDATE \`alert_events\` SET \`last_updated\`=? WHERE \`event_id\`=?;`,
            payload: [now_sql, eventToUpdate]
        };
    }
}

states[dedup_key] = state;
flow.set('alert_states', states);

if (outputs.some(o => o !== null)) {
    return outputs;
}
return null;
